mapper.update:
UPDATE im_friend_request
SET status = #{status}, update_time = NOW()
WHERE from_user = #{fromUser} AND to_user = #{toUser}

mapper.select:
SELECT * FROM im_friend_request
WHERE from_user = #{userId}
ORDER BY create_time DESC

SELECT * FROM im_friend_request
WHERE to_user = #{userId}
ORDER BY create_time DESC
